const fs = require('fs');
const errors = [];

// Try to parse the files as JavaScript
const files = [
  'src/components/query/empty-state.tsx',
  'src/app/query/page.tsx',
  'src/components/query/result-table.tsx'
];

files.forEach(file => {
  try {
    const content = fs.readFileSync(file, 'utf-8');
    // Basic syntax check
    if (content.includes('import ') || content.includes('export ') || content.includes('function') || content.includes('return')) {
      console.log(file + ': Appears valid');
    }
  } catch (e) {
    errors.push({file, error: e.message});
  }
});

if (errors.length > 0) {
  console.error('Errors:', errors);
  process.exit(1);
}
")
